tinymce.PluginManager.add('youtube', function (editor) {
    function openmanager() {
        var title = "Youtube videó kiválasztása";

        win = editor.windowManager.open({
            title: title,
            file: base_url + 'admin/youtube_init',
            filetype: 'video',
            width: 785,
            height: 590,
            inline: 1,
            buttons: [{
                text: 'cancel',
                onclick: function () {
                    this.parent().parent().close();
                }
            }]
        });
    }
    editor.addButton('youtube', {
        icon: true,
        image: tinyMCE.baseURL + '/plugins/youtube/icon.png',
        tooltip: 'Youtube videó beszúrása/szerkesztése',
        shortcut: 'Ctrl+Q',
        onclick: openmanager
    });
    editor.addShortcut('Ctrl+Q', '', openmanager);
    editor.addMenuItem('youtube', {
        icon: 'media',
        text: 'Youtube videó beszúrása',
        shortcut: 'Ctrl+Q',
        onclick: openmanager,
        context: 'insert'
    });
});